components.calculateValue:
var steps = {
  '1': [10, 24],
  '2': [12, 20, 24]
}

var equipment = {
  lifeJackets: { '1': [2, 2], '2': [2, 2, null] },
  lifeRafts: { '1': [], '2': [] },
  lifeBuoysLine: { '1': [], '2': [2, 2, null] },
  lifeBuoysLineSmoke: { '1': [2, 2], '2': [] },
  lifeBuoysSelfIgniting: { '1': [], '2': [] },
  parachuteFlares: { '1': [3, 3], '2': [4, 4, null] },
  handheldFlares: { '1': [3, 3], '2': [4, 4, null] },
  smokeSignals: { '1': [2, 2], '2': [2, 2, null] },
  fireBucketsLanyard: { '1': [1, 1], '2': [1, 1, null] },
  fireExtinguishersMulti: { '1': [2, 2], '2': [3, 3, null] },
  firePumpsHoses: { '1': [], '2': [1, 1, null] },
  fireExtinguishersFixed: { '1': [], '2': [] },
  vhfRadios: { '1': [2, 2], '2': [2, 2, null] },
  bilgePumps: { '1': [1, 1], '2': [1, 1, null] },
  bilgeAlarms: { '1': [], '2': [1, 1, null] },
  navLightsSoundSignals: { '1': [1, 1], '2': [1, 1, null] },
  compass: { '1': [1, 1], '2': [1, 1, null] },
  waterproofTorchs: { '1': [1, 1], '2': [1, 1, null] },
  firstAidKits: { '1': [1, 1], '2': [1, 1, null] },
  bailers: { '1': [1, 1], '2': [] },
  oarsPaddles: { '1': [2, 2], '2': [2, null, null] }
};

var getMinRequired = function(l, t, k) {
  if (equipment[k][t]) {
    if (steps[t]) {
      for (var i = 0; i < steps[t].length; i++) {
        if (l < steps[t][i] && equipment[k][t]) {
          return equipment[k][t][i] || false;
        }
      }
    }
  }
  return false;
};

if (data.craftType && data.loa) {
  for (var key in equipment) {
    var min = getMinRequired(data.loa, data.craftType, key);
    if (min) {
      console.log(key, min);
      var el = document.querySelector('.safety-at-sea-' + key + ' span');
      if (el) {
        el.innerText = min;
      }
    }
  }
}

